contracts/Governance.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract Governance is Ownable, Pausable {
    struct Proposal {
        uint256 id;
        string description;
        uint256 votesFor;
        uint256 votesAgainst;
        uint256 endTime;
        uint256 executionTime;
        bool executed;
        bool canceled;
        address proposer;
        address targetContract;
        bytes data;
    }

    uint256 public proposalCount;
    uint256 public votingPeriod = 5 days; // Default 5-day voting period
    uint256 public executionDelay = 1 days; // Delay before execution after voting ends

    mapping(uint256 => Proposal) public proposals;
    mapping(uint256 => mapping(address => bool)) public hasVoted;
    mapping(address => bool) public isWhitelisted;

    IERC20 public governanceToken;

    event ProposalCreated(uint256 proposalId, string description, address proposer, uint256 endTime);
    event Voted(uint256 proposalId, address voter, bool vote);
    event ProposalExecuted(uint256 proposalId);
    event ProposalCanceled(uint256 proposalId);
    event Whitelisted(address indexed user);
    event RemovedFromWhitelist(address indexed user);
    event VotingPeriodUpdated(uint256 newPeriod);
    event ExecutionDelayUpdated(uint256 newDelay);

    // ✅ Fixed Constructor (Merged Both Into One)
    constructor(address initialOwner, address _governanceToken) Ownable(initialOwner) {
        governanceToken = IERC20(_governanceToken);
    }

    /** ✅ Whitelist Functions */
    function addWhitelisted(address user) external onlyOwner {
        isWhitelisted[user] = true;
        emit Whitelisted(user);
    }

    function removeWhitelisted(address user) external onlyOwner {
        isWhitelisted[user] = false;
        emit RemovedFromWhitelist(user);
    }

    /** ✅ Create Proposal */
    function createProposal(string memory description, address targetContract, bytes memory data) external whenNotPaused {
        require(isWhitelisted[msg.sender], "Not whitelisted to create proposals");
        require(governanceToken.balanceOf(msg.sender) >= 100 * 10**18, "Must hold at least 100 LYTH to propose");

        uint256 proposalId = proposalCount++;
        proposals[proposalId] = Proposal({
            id: proposalId,
            description: description,
            votesFor: 0,
            votesAgainst: 0,
            endTime: block.timestamp + votingPeriod,
            executionTime: 0,
            executed: false,
            canceled: false,
            proposer: msg.sender,
            targetContract: targetContract,
            data: data
        });

        emit ProposalCreated(proposalId, description, msg.sender, proposals[proposalId].endTime);
    }

    /** ✅ Vote on Proposal */
    function vote(uint256 proposalId, bool voteFor) external whenNotPaused {
        Proposal storage proposal = proposals[proposalId];

        require(!proposal.canceled, "Proposal was canceled");
        require(!hasVoted[proposalId][msg.sender], "Already voted");
        require(block.timestamp <= proposal.endTime, "Voting period expired");

        uint256 votingPower = governanceToken.balanceOf(msg.sender);
        require(votingPower > 0, "Must hold governance tokens to vote");

        hasVoted[proposalId][msg.sender] = true;
        if (voteFor) {
            proposal.votesFor += votingPower;
        } else {
            proposal.votesAgainst += votingPower;
        }

        emit Voted(proposalId, msg.sender, voteFor);
    }

    /** ✅ Finalize Proposal (Sets Execution Time) */
    function finalizeProposal(uint256 proposalId) external whenNotPaused {
        Proposal storage proposal = proposals[proposalId];

        require(!proposal.canceled, "Proposal was canceled");
        require(proposal.votesFor > proposal.votesAgainst, "Proposal did not pass");
        require(block.timestamp > proposal.endTime, "Voting period not ended");

        proposal.executionTime = block.timestamp + executionDelay;
    }

    /** ✅ Execute Proposal */
    function executeProposal(uint256 proposalId) external onlyOwner whenNotPaused {
        Proposal storage proposal = proposals[proposalId];

        require(!proposal.executed, "Already executed");
        require(!proposal.canceled, "Proposal was canceled");
        require(block.timestamp >= proposal.executionTime, "Execution delay active");

        proposal.executed = true;
        (bool success, ) = proposal.targetContract.call(proposal.data);
        require(success, "Proposal execution failed");

        emit ProposalExecuted(proposalId);
    }

    /** ✅ Cancel Proposal */
    function cancelProposal(uint256 proposalId) external whenNotPaused {
        Proposal storage proposal = proposals[proposalId];

        require(msg.sender == proposal.proposer || msg.sender == owner(), "Only proposer or owner can cancel");
        require(!proposal.executed, "Cannot cancel executed proposal");

        proposal.canceled = true;
        emit ProposalCanceled(proposalId);
    }

    /** ✅ Admin Functions */
    function setVotingPeriod(uint256 newPeriod) external onlyOwner whenNotPaused {
        require(newPeriod >= 1 days && newPeriod <= 14 days, "Voting period out of range");
        votingPeriod = newPeriod;
        emit VotingPeriodUpdated(newPeriod);
    }

    function setExecutionDelay(uint256 newDelay) external onlyOwner whenNotPaused {
        require(newDelay >= 1 hours && newDelay <= 7 days, "Execution delay out of range");
        executionDelay = newDelay;
        emit ExecutionDelayUpdated(newDelay);
    }

    function pauseGovernance() external onlyOwner {
        _pause();
    }

    function unpauseGovernance() external onlyOwner {
        _unpause();
    }
}

contracts/Mintium.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

interface INFTMarketplace {
    function mintNFT(address to, uint256 amount) external;
}

interface ILiquidityPool {
    function depositLiquidity(address user, uint256 amount) external;
    function withdrawRewards(address user) external;
}

interface IMultiSigTreasury {
    function approveTransaction(address recipient, uint256 amount) external;
}

contract Mintium is ERC20, ERC20Burnable, Ownable, ReentrancyGuard {

    uint256 public transactionFee = 2; // 2% Transaction Fee
    uint256 public stakingRewardRate = 5; // 5% Staking Reward Rate
    uint256 public totalStaked; // Total Staked MNTM

    address public treasury;
    address public nftMarketplace;
    address public liquidityPool;

    mapping(address => uint256) public stakingBalances;
    mapping(address => uint256) public stakingStartTime;
    mapping(address => bool) public isExcludedFromFees;

    IMultiSigTreasury public treasuryContract;

    event TokensStaked(address indexed user, uint256 amount);
    event TokensUnstaked(address indexed user, uint256 amount);
    event FeeUpdated(uint256 newFee);
    event StakingRewardUpdated(uint256 newRewardRate);
    event TreasuryUpdated(address newTreasury);
    event NFTMinted(address indexed user, uint256 amount);
    event LiquidityDeposited(address indexed user, uint256 amount);
    event LiquidityWithdrawn(address indexed user, uint256 amount);
    event ExcludedFromFees(address indexed user, bool isExcluded);

    /** ✅ Constructor: Set Initial Owner & Treasury */
    constructor(address initialOwner, address _treasury) 
        ERC20("Mintium", "MNTM") 
        Ownable(initialOwner) 
    {
        _mint(initialOwner, 100_000_000 * 10 ** decimals());
        treasuryContract = IMultiSigTreasury(_treasury);
        treasury = _treasury;  // ✅ Ensure treasury is set
        isExcludedFromFees[initialOwner] = true;
    }

    /** ✅ Fix: Override `_update` instead of `_transfer` */
    function _update(address from, address to, uint256 value) internal override {
        require(to != address(0), "Transfer to zero address is not allowed");

        if (isExcludedFromFees[from] || isExcludedFromFees[to]) {
            super._update(from, to, value);
        } else {
            uint256 feeAmount = (value * transactionFee) / 100;
            uint256 transferAmount = value - feeAmount;

            super._update(from, treasury, feeAmount); // Send fees to treasury
            super._update(from, to, transferAmount); // Send remaining to recipient
        }
    }

    /** ✅ Update Transaction Fee */
    function setTransactionFee(uint256 newFee) external onlyOwner {
        require(newFee <= 10, "Fee too high"); // Max 10%
        transactionFee = newFee;
        emit FeeUpdated(newFee);
    }

    /** ✅ Exclude Addresses from Fees */
    function setExcludedFromFees(address user, bool exclude) external onlyOwner {
        isExcludedFromFees[user] = exclude;
        emit ExcludedFromFees(user, exclude);
    }

    /** ✅ Set Staking Reward Rate */
    function setStakingRewardRate(uint256 newRate) external onlyOwner {
        require(newRate <= 20, "Reward rate too high");
        stakingRewardRate = newRate;
        emit StakingRewardUpdated(newRate);
    }

    /** ✅ Stake Tokens */
    function stake(uint256 amount) external nonReentrant {
        require(amount > 0, "Cannot stake 0");
        _transfer(msg.sender, address(this), amount);

        stakingBalances[msg.sender] += amount;
        stakingStartTime[msg.sender] = block.timestamp;
        totalStaked += amount;

        emit TokensStaked(msg.sender, amount);
    }

    /** ✅ Unstake Tokens */
    function unstake() external nonReentrant {
        require(stakingBalances[msg.sender] > 0, "No staked balance");

        uint256 amount = stakingBalances[msg.sender];
        stakingBalances[msg.sender] = 0;
        totalStaked -= amount;

        _transfer(address(this), msg.sender, amount);
        emit TokensUnstaked(msg.sender, amount);
    }

    /** ✅ Claim Staking Rewards */
    function claimStakingRewards() external nonReentrant {
        require(stakingBalances[msg.sender] > 0, "No staked balance");

        uint256 stakedDuration = block.timestamp - stakingStartTime[msg.sender];
        uint256 rewardAmount = (stakingBalances[msg.sender] * stakingRewardRate * stakedDuration) / (365 days * 100);

        _mint(msg.sender, rewardAmount);
        stakingStartTime[msg.sender] = block.timestamp;

        emit StakingRewardUpdated(stakingRewardRate);
    }

    /** ✅ Deposit Liquidity */
    function depositLiquidity(uint256 amount) external nonReentrant {
        require(liquidityPool != address(0), "Liquidity pool not set");
        require(balanceOf(msg.sender) >= amount, "Insufficient balance");

        _transfer(msg.sender, liquidityPool, amount);
        ILiquidityPool(liquidityPool).depositLiquidity(msg.sender, amount);
        emit LiquidityDeposited(msg.sender, amount);
    }

    /** ✅ Withdraw Liquidity Rewards */
    function withdrawLiquidityRewards() external nonReentrant returns (bool) {
        require(liquidityPool != address(0), "Liquidity pool not set");
        ILiquidityPool(liquidityPool).withdrawRewards(msg.sender);
        emit LiquidityWithdrawn(msg.sender, stakingBalances[msg.sender]);
        return true;
    }
}

contracts/NFTMarketplace.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/interfaces/IERC2981.sol";

interface IMultiSigTreasury {
    function approveTransaction(address recipient, uint256 amount) external;
}

contract NFTMarketplace is ERC721, Ownable, Pausable, IERC2981 {
    uint256 public nextTokenId;
    address public mintiumToken;
    address public treasury;
    uint256 public mintPrice = 100 * 10**18; // Default 100 MNTM
    uint256 public tradingFee = 250; // 2.5% fee on sales (in basis points)
    uint256 public listingDuration = 30 days; // Default listing expiration
    uint256 public royaltyFee = 500; // 5% creator royalty (in basis points)

    struct Listing {
        address seller;
        uint256 price;
        uint256 expiration;
        bool isListed;
    }

    mapping(uint256 => Listing) public listings;
    mapping(uint256 => address) public creators;
    IMultiSigTreasury public treasuryContract;

    event NFTMinted(address indexed owner, uint256 tokenId);
    event NFTListed(uint256 tokenId, uint256 price, uint256 expiration);
    event NFTPurchased(uint256 tokenId, address buyer);
    event NFTListingCancelled(uint256 tokenId);
    event MintPriceUpdated(uint256 newPrice);
    event TradingFeeUpdated(uint256 newFee);
    event RoyaltyFeeUpdated(uint256 newFee);

    /** ✅ Constructor */
    constructor(address initialOwner, address _mintiumToken, address _treasury) 
        ERC721("SolythraNFT", "SNFT") 
        Ownable(initialOwner) 
    {
        mintiumToken = _mintiumToken;
        treasury = _treasury;
        treasuryContract = IMultiSigTreasury(_treasury);
    }

    /** ✅ Update Minting & Trading Fees */
    function setMintPrice(uint256 _mintPrice) external onlyOwner {
        mintPrice = _mintPrice;
        emit MintPriceUpdated(_mintPrice);
    }

    function setTradingFee(uint256 _newFee) external onlyOwner {
        require(_newFee <= 500, "Fee too high"); // Max 5%
        tradingFee = _newFee;
        emit TradingFeeUpdated(_newFee);
    }

    function setRoyaltyFee(uint256 _newFee) external onlyOwner {
        require(_newFee <= 1000, "Royalty too high"); // Max 10%
        royaltyFee = _newFee;
        emit RoyaltyFeeUpdated(_newFee);
    }

    /** ✅ Mint NFT */
    function mintNFT() external whenNotPaused {
        require(IERC20(mintiumToken).balanceOf(msg.sender) >= mintPrice, "Insufficient MNTM");
        require(IERC20(mintiumToken).transferFrom(msg.sender, treasury, mintPrice), "MNTM transfer failed");

        creators[nextTokenId] = msg.sender; // Store creator
        _mint(msg.sender, nextTokenId);
        emit NFTMinted(msg.sender, nextTokenId);
        nextTokenId++;
    }

    /** ✅ Batch Minting */
    function batchMintNFT(uint256 amount) external whenNotPaused {
        require(amount > 0, "Must mint at least 1 NFT");
        uint256 totalCost = mintPrice * amount;
        require(IERC20(mintiumToken).balanceOf(msg.sender) >= totalCost, "Insufficient MNTM");
        require(IERC20(mintiumToken).transferFrom(msg.sender, treasury, totalCost), "MNTM transfer failed");

        for (uint256 i = 0; i < amount; i++) {
            creators[nextTokenId] = msg.sender;
            _mint(msg.sender, nextTokenId);
            emit NFTMinted(msg.sender, nextTokenId);
            nextTokenId++;
        }
    }

    /** ✅ List NFT */
    function listNFT(uint256 tokenId, uint256 price) external whenNotPaused {
        require(ownerOf(tokenId) == msg.sender, "Not NFT owner");
        listings[tokenId] = Listing(msg.sender, price, block.timestamp + listingDuration, true);
        emit NFTListed(tokenId, price, listings[tokenId].expiration);
    }

    /** ✅ Cancel Listing */
    function cancelListing(uint256 tokenId) external whenNotPaused {
        require(ownerOf(tokenId) == msg.sender, "Not NFT owner");
        require(listings[tokenId].isListed, "NFT not listed");

        listings[tokenId].isListed = false;
        emit NFTListingCancelled(tokenId);
    }

    /** ✅ Buy NFT */
    function buyNFT(uint256 tokenId) external whenNotPaused {
        require(listings[tokenId].isListed, "NFT not listed");
        require(block.timestamp <= listings[tokenId].expiration, "Listing expired");

        uint256 feeAmount = (listings[tokenId].price * tradingFee) / 10000;
        uint256 royaltyAmount = (listings[tokenId].price * royaltyFee) / 10000;
        uint256 sellerAmount = listings[tokenId].price - feeAmount - royaltyAmount;

        require(IERC20(mintiumToken).transferFrom(msg.sender, listings[tokenId].seller, sellerAmount), "Payment failed");
        require(IERC20(mintiumToken).transferFrom(msg.sender, creators[tokenId], royaltyAmount), "Royalty payment failed");
        require(IERC20(mintiumToken).transferFrom(msg.sender, treasury, feeAmount), "Fee transfer failed");

        _transfer(listings[tokenId].seller, msg.sender, tokenId);
        listings[tokenId].isListed = false;
        emit NFTPurchased(tokenId, msg.sender);
    }

    /** ✅ ERC2981 Royalty Info */
    function royaltyInfo(uint256 tokenId, uint256 salePrice) external view override returns (address, uint256) {
        uint256 royaltyAmount = (salePrice * royaltyFee) / 10000;
        return (creators[tokenId], royaltyAmount);
    }

    /** ✅ Pause Marketplace */
    function pauseMarketplace() external onlyOwner {
        _pause();
    }

    function unpauseMarketplace() external onlyOwner {
        _unpause();
    }
}

contracts/Solythis.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/utils/cryptography/EIP712.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

interface IMultiSigTreasury {
    function approveTransaction(address recipient, uint256 amount) external;
}

contract Solythis is ERC20, Ownable, ReentrancyGuard, Pausable, EIP712 {

    struct Proposal {
        uint256 id;
        string description;
        uint256 votesFor;
        uint256 votesAgainst;
        bool executed;
        uint256 executionTime;
        address proposer;
        address targetContract;
        bytes data;
    }

    uint256 private _proposalIds;
    mapping(uint256 => Proposal) public proposals;
    mapping(uint256 => mapping(address => bool)) public hasVoted;

    mapping(address => uint256) private _stakedLYTH;
    mapping(address => uint256) private _stakeTimestamp;
    mapping(address => bool) public isWhitelisted;

    IMultiSigTreasury public treasury;
    uint256 public stakingRewardRate = 5;  // Default 5% yearly

    event ProposalCreated(uint256 proposalId, string description, address proposer);
    event Voted(uint256 proposalId, address voter, bool vote);
    event ProposalExecuted(uint256 proposalId);
    event Staked(address indexed user, uint256 amount);
    event Unstaked(address indexed user, uint256 amount);
    event FundsApproved(address indexed recipient, uint256 amount);
    event StakingRewardUpdated(uint256 newRewardRate);
    event Whitelisted(address indexed user);
    event RemovedFromWhitelist(address indexed user);

    constructor(address initialOwner, address _treasury) 
        ERC20("Solythis", "LYTH") 
        Ownable(initialOwner) 
        EIP712("Solythis", "1") 
    {
        _mint(initialOwner, 1_000_000_000 * 10 ** decimals());
        treasury = IMultiSigTreasury(_treasury);
    }

    /** 🔹 Governance: Whitelist Management */
    function addWhitelisted(address user) external onlyOwner {
        isWhitelisted[user] = true;
        emit Whitelisted(user);
    }

    function removeWhitelisted(address user) external onlyOwner {
        isWhitelisted[user] = false;
        emit RemovedFromWhitelist(user);
    }

    /** 🔹 Governance: Create Proposal */
    function createProposal(string memory description, address targetContract, bytes memory data) external whenNotPaused {
        require(isWhitelisted[msg.sender], "Not whitelisted to create proposals");
        require(balanceOf(msg.sender) > 0, "Must hold LYTH to propose");

        uint256 proposalId = _proposalIds++;
        proposals[proposalId] = Proposal({
            id: proposalId,
            description: description,
            votesFor: 0,
            votesAgainst: 0,
            executed: false,
            executionTime: 0,
            proposer: msg.sender,
            targetContract: targetContract,
            data: data
        });

        emit ProposalCreated(proposalId, description, msg.sender);
    }

    /** 🔹 Governance: Vote on Proposal */
    function voteOnProposal(uint256 proposalId, bool voteFor) external whenNotPaused {
        require(_stakedLYTH[msg.sender] > 0, "Must have staked LYTH to vote");
        require(!hasVoted[proposalId][msg.sender], "Already voted");

        uint256 votingPower = _stakedLYTH[msg.sender];
        if (voteFor) {
            proposals[proposalId].votesFor += votingPower;
        } else {
            proposals[proposalId].votesAgainst += votingPower;
        }

        hasVoted[proposalId][msg.sender] = true;
        emit Voted(proposalId, msg.sender, voteFor);
    }

    /** 🔹 Governance: Execution Delay */
    function finalizeProposal(uint256 proposalId) external whenNotPaused {
        Proposal storage proposal = proposals[proposalId];
        require(proposal.votesFor > proposal.votesAgainst, "Proposal did not pass");
        proposal.executionTime = block.timestamp + 1 days; // Mandatory 24-hour execution delay
    }

    function executeProposal(uint256 proposalId) external onlyOwner whenNotPaused nonReentrant {
        Proposal storage proposal = proposals[proposalId];
        require(!proposal.executed, "Already executed");
        require(block.timestamp >= proposal.executionTime, "Execution delay active");
        require(proposal.votesFor > proposal.votesAgainst, "Not enough votes to pass");

        proposal.executed = true;
        (bool success, ) = proposal.targetContract.call(proposal.data);
        require(success, "Proposal execution failed");

        emit ProposalExecuted(proposalId);
    }

    /** 🔹 Staking Functions */
    function stakeTokens(uint256 amount) external whenNotPaused nonReentrant {
        require(amount > 0, "Cannot stake 0");
        _transfer(msg.sender, address(this), amount);
        _stakedLYTH[msg.sender] += amount;
        _stakeTimestamp[msg.sender] = block.timestamp;

        emit Staked(msg.sender, amount);
    }

    function unstakeTokens() external whenNotPaused nonReentrant {
        require(_stakedLYTH[msg.sender] > 0, "No staked LYTH");
        uint256 amount = _stakedLYTH[msg.sender];
        _stakedLYTH[msg.sender] = 0;
        _transfer(address(this), msg.sender, amount);

        emit Unstaked(msg.sender, amount);
    }

    function claimStakingRewards() external whenNotPaused nonReentrant {
        require(_stakedLYTH[msg.sender] > 0, "No staked LYTH");
        uint256 stakedTime = block.timestamp - _stakeTimestamp[msg.sender];
        uint256 rewardAmount = (_stakedLYTH[msg.sender] * stakingRewardRate * stakedTime) / (365 days * 100);

        _mint(msg.sender, rewardAmount);
        _stakeTimestamp[msg.sender] = block.timestamp;

        emit StakingRewardUpdated(stakingRewardRate);
    }

    /** 🔹 Treasury Fund Approvals */
    function executeTreasuryTransaction(uint256 proposalId, address recipient, uint256 amount) external whenNotPaused nonReentrant {
        Proposal storage proposal = proposals[proposalId];
        require(proposal.executed, "Proposal not executed yet");

        treasury.approveTransaction(recipient, amount);
        emit FundsApproved(recipient, amount);
    }

    /** 🔹 Admin Functions */
    function setStakingRewardRate(uint256 newRate) external onlyOwner whenNotPaused {
        require(newRate <= 20, "Reward rate too high");
        stakingRewardRate = newRate;
        emit StakingRewardUpdated(newRate);
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }
}

